  \fill[seq4!30, nearly transparent] (-3,-2) rectangle (4,3);
  \node[seq4] at (.5,2.5) {$\Span\{v,w\}$};
  \draw[vector, seq1] (0,-1) to["$v$"] (1,1);
  \draw[vector, seq2] (0,-1) to["\strut$w$"'] (1,-1);
  \point at (0,-1);
\end{scope}

